import React from 'react';
import { 
//AudioMutedOutlined,
ArrowLeftOutlined,
StepBackwardOutlined,
StepForwardOutlined,
PlayCircleFilled,
SoundOutlined,
PauseCircleFilled
} from '@ant-design/icons';
//import { Button } from 'antd';
import "./content.css";

var playn = function () {
    return (
        <div className="play"><PlayCircleFilled style={{color:"black"}}/></div>
    )
}

var pausen = function () {
    return (
        <div className="pause"><PauseCircleFilled style={{color:"black"}}/></div>
    )
}

function getDom(selector) {
    return document.querySelectorAll(selector);
}

function init() {
    var audio = new Audio("./NewRomantics.flac");

    var playBtn = document.getElementById("playBox"),
        progressBox = getDom(".progressBox")[0],
        progressBar = getDom(".progressBar")[0],
        curTime = getDom(".curTime")[0],
        songTime = getDom(".totalTime")[0],
        singerName = getDom("#singerName")[0],
        songName = getDom("#songName")[0],
        volume = getDom(".volume")[0],
        volBar = getDom("#volBar")[0],
        muteBtn = getDom("#muteBtn");

    playBtn.addEventListener("click", function() {
        mainPlay()
    }, false)


    var isDrag = false;
    var index = 0;
    var audio_m = 230.4,
        audio_s = 0,
        curH = 0,
        curM = 0,
        curS = 0;
    var musicData = [
        {
            "src":"./NewRomantics.flac",
            "bg":"./musicDisc.jpg",
            "songNmae": "New Romantics",
            "singerName": "Taylor Swift",
            "lm": 3,
            "ls": 50
        }
    ]

    function initPlay() {
        audio.pause();
        audio.volume = Math.round(volBar.offsetWidth / volume.offsetWidth * 100) / 100;
    }

    function mainPlay() {
        let temp = document.getElementById("playBox").getElementsByTagName("div")[0]
        console.log(temp)
        if(temp.className === "play"){
            //playBtn = <div className="playBox">{ pausen() }</div>
            audio.play()
        }else{
            //playBtn = <div className="playBox">{ playn() }</div>
            audio.pause()
        }
    }

    /*
    audio.addEventListener("timeupdate", function() {
        console.log("1")
        if(!isDrag){

            curM = Math.floor(audio.currentTime / 60);
            curS = Math.round(audio.currentTime - curM * 60);
            curM = curM < 10 ? "0" + curM : curM;
            curS = curS < 10 ? "0" + curS : curS;
            curTime.innerHTML = curM + ":" + curS;

            var curLong = Math.round(audio.currentTime / audio.duration * (progressBox.offsetWidth - 16))
            progressBar.style.width = curLong + "px";

        }
    }, false)
    */
    initPlay()
}

class Content extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            isPlay: false,
            isMuted: false,
            volume: 100,
            totalTime: 0,
            currentTime: 0,
        };
    }

    play = () => {
        this.setState({
            isPlay: !this.state.isPlay
        })
    }

    millisecondToDate(time) {
        const second = Math.floor(time % 60);
        let minite = Math.floor(time / 60);
        return `${minite}:${second >= 10 ? second : `0${second}`}`;
    }

    controlAudio = (type, value) => {
        console.log(type, value)
        const audio = document.getElementById("audio");
        switch(type) {
            case 'totalTime':
                this.setState({
                    totalTime: audio.duration
                });
                break;
            case 'play':
                audio.play();
                this.setState({
                    isPlay: true
                });
                break;
            case 'pause':
                audio.pause();
                this.setState({
                    isPlay: false
                });
                break;
            case 'muted':
                this.setState({
                    isMuted: !audio.muted
                });
                audio.muted = !audio.muted;
                break;
            case 'changeCurrentTime':
                this.setState({
                    currentTime: value
                });

                audio.currentTime = value;
                if(value === audio.duration) {
                    this.setState({
                        isPlay: false
                    });
                }
                break;
            case 'getCurrentTime':
                this.setState({
                    currentTime: audio.currentTime
                });

                if (audio.currentTime === audio.duration) {
                    this.setState({
                        isPlay: false
                    });
                }
                break;
            case 'changeVolume':
                audio.volume = value / 100;
                this.setState({
                    volume: value,
                    isMuted: !value
                });
                break;
            default:
                break;
        }
    }

    handlePlay = () =>{
        let { isPlay } = this.state
        let controlAudio = this.controlAudio
        document.getElementById("playBox").addEventListener("click", function () {
            if(isPlay){
                controlAudio("pause", 0)
            }else{
                controlAudio("play", 0)
            }
        })
    }

    componentDidMount() {
        this.handlePlay()
    }

    render() {
        let {isPlay, isMuted, volume, totalTime, currentTime} = this.state;
        let audio_time = currentTime / totalTime * 100;

        return(
            <div>
                <div className="main">
                    <audio
                        id="audio"
                        src="./NewRomantics.flac"
                        preload="true"
                        onCanPlay={() => this.controlAudio('totalTime')}
                        onTimeUpdate={(e) => this.controlAudio('getCurrentTime')}
                    >
                        您的浏览器不支持 audio 标签。
                    </audio>
                    <div className="headBox clearFix">
                        <div className="goBack">
                        <ArrowLeftOutlined />
                        </div>
				        <div className="title">
					        <p className="songName" id="songName">New Romantics</p>
					        <p className="singer" id="singerName">Taylor Swift</p>
				        </div>
                    </div>
                    <div className="discBox">
				        <div className="volume" id="volume">
					        <div className="volBar" id="volBar">
						        <span className="volArc"></span>
					        </div>
					        <div id="muteBtn"><i id="volumeTag"><SoundOutlined /></i></div>
                            <div className="border"></div>
                            <div className="disc"></div>
                        </div>
				    </div>
                    <div className="controlBox">
                        <div className="progressBox">
                            <div className="curTime">0:00</div>
                            <div className="progressBar">
                                <span className="progressArc"></span>
                            </div>
                            <div className="totalTime">3:50</div>
                        </div>
                        <div className="prev"><StepBackwardOutlined style={{color:"black"}}/></div>
                        <div id="playBox">{ playn() }</div>
                        <div className="next"><StepForwardOutlined style={{color:"black"}}/></div>
                    </div>
                </div>
                <div className="blur">
		        </div>
            </div>
        )
    }
}

export default Content;
