import React from 'react';
import { 
//AudioMutedOutlined,
ArrowLeftOutlined,
StepBackwardOutlined,
StepForwardOutlined,
PlayCircleFilled,
SoundOutlined,
PauseCircleFilled
} from '@ant-design/icons';
//import { Button } from 'antd';
import "./content.css";

var formatTime = function(seconds) {
    var h = 0,
        i = 0,
        s = Math.floor(seconds);
        h = Math.floor(s / 3600);
        i = Math.floor((s % 3600) / 60);
        s = s % 3600 % 60

    return{
        H: h = h < 10 ? "0" + h : h,
        I: i = i < 10 ? "0" + i : i,
        S: s = s < 10 ? "0" + s : s
    }
}

var playn = function () {
    return (
        <div className="play"><PlayCircleFilled style={{color:"black"}}/></div>
    )
}

var pausen = function () {
    return (
        <div className="pause"><PauseCircleFilled style={{color:"black"}}/></div>
    )
}

function getDom(selector) {
    return document.querySelectorAll(selector);
}

function bindEvent(ele, evnetType, callback) {
    if(ele === undefined){
        console.log("bindEvent undefined")
    }else if(typeof ele.addEventListener == "function"){
        ele.addEventListener(evnetType, callback, false)
    }else if(typeof ele.attachEvent == "function"){
        ele.attachEvent(evnetType, callback, false)
    }else{
        ele['on' + evnetType] = callback
    }
}

function init() {
    console.log("-----------------");
    var audio = new Audio("./musicDisc.jpg");
    var playBtn = getDom(".playBox")[0],
        progressBox = getDom(".progressBox")[0],
        progressBar = getDom(".progressBar")[0],
        curTime = getDom(".curTime")[0],
        songTime = getDom(".totalTime")[0],
        singerName = getDom("#singerName")[0],
        songName = getDom("#songName")[0],
        volume = getDom(".volume")[0],
        volBar = getDom("#volBar")[0],
        muteBtn = getDom("#muteBtn");

    var isDrag = false;
    var index = 0;
    var audio_m = 230.4,
        audio_s = 0,
        curH = 0,
        curM = 0,
        curS = 0;
    var musicData = [
        {
            "src":"./NewRomantics.flac",
            "bg":"./musicDisc.jpg",
            "songNmae": "New Romantics",
            "singerName": "Taylor Swift",
            "lm": 3,
            "ls": 50
        }
    ]

    function initPlay() {
        audio.pause();
        audio.volume = Math.round(volBar.offsetWidth / volume.offsetWidth * 100) / 100;
        playBtn = <div className="playBox">{ playn() }</div>
        audio.pause()
    }

    function mainPlay() {
        let temp = playBtn.getElementsByTagName("div")[0]
        if(temp.className === "play"){
            playBtn = <div className="playBox">{ pausen() }</div>
            audio.play()
        }else{
            playBtn = <div className="playBox">{ playn() }</div>
            audio.pause()
        }
    }

    bindEvent(audio, "timeupdate", function() {
        if(!isDrag){
            curM = Math.floor(audio.currentTime / 60);
            curS = Math.round(audio.currentTime - curM * 60);
            curM = curM < 10 ? "0" + curM : curM;
            curS = curS < 10 ? "0" + curS : curS;
            curTime.innerHTML = curM + ":" + curS;

            var curLong = Math.round(audio.currentTime / audio.duration * (progressBox.offsetWidth - 16))
            progressBar.style.width = curLong + "px";
        }
    })

    initPlay()
    bindEvent(playBtn, "click", function() {
        mainPlay()
    })
}

class Content extends React.Component {

    componentDidMount() {
        init()
    }

    render() {
        return(
            <div>
                <div className="main">
                    <div className="headBox clearFix">
                        <div className="goBack">
                        <ArrowLeftOutlined />
                        </div>
				        <div className="title">
					        <p className="songName" id="songName">New Romantics</p>
					        <p className="singer" id="singerName">Taylor Swift</p>
				        </div>
                    </div>
                    <div className="discBox">
				        <div className="volume" id="volume">
					        <div className="volBar" id="volBar">
						        <span className="volArc"></span>
					        </div>
					        <div id="muteBtn"><i id="volumeTag"><SoundOutlined /></i></div>
                            <div className="border"></div>
                            <div className="disc"></div>
                        </div>
				    </div>
                    <div className="controlBox">
                        <div className="progressBox">
                            <div className="curTime">0:00</div>
                            <div className="progressBar">
                                <span className="progressArc"></span>
                            </div>
                            <div className="totalTime">3:50</div>
                        </div>
                        <div className="prev"><StepBackwardOutlined style={{color:"black"}}/></div>
                        <div className="playBox">{ playn() }</div>
                        <div className="next"><StepForwardOutlined style={{color:"black"}}/></div>
                    </div>
                </div>
                <div className="blur">
		        </div>
            </div>
        )
    }
}

export default Content;
